class stake_data(stake_dataTemplate):
  def __init__(self, **properties):
    # Set Form properties and Data Bindings.
    self.init_components(**properties)
    data = self.get_early_end_stake_data()
    for chain, stake_data in data.items():
      title = Label(text=chain, role='text', underline=True)
      self.add_component(title)
      for k,v in stake_data.items():
        if "HDRN" in k:
          token = "HDRN"
        elif "HEX" in k:
          token = "HEX"
        else:
          token = "TEAM"
        if token == "HEX":
          rt =RichText(content="<b>{}: </b>{:.8f} {}".format(k,v, token), format='restricted_html')
        else:
          if "TEAM per TEAM" in k:
            rt =RichText(content="<b>{}: </b>{:.8f} {}".format(k,v, token), format='restricted_html')
          else:
            rt =RichText(content="<b>{}: </b>{:,} {}".format(k,int(v), token), format='restricted_html')
        self.add_component(rt)
